const config = $input.first().json;
const folderPath = config.folder_path;
const targetStage = config.target_stage;
const forceReprocess = config.force_reprocess;

const videoFiles = [
  'BB580.mp4',
  'CI166.mp4',
  'CJ335.mp4',
  'CP557.mp4',
  'EMP140.mp4',
  'MG096.mp4',
  'OSS096.mp4',
  'SL031.mp4'
];

const episodes = videoFiles.map(filename => {
  const baseName = filename.replace(/\.[^/.]+$/, '');
  const episodeId = 'newsroom-2024-' + baseName.toLowerCase().replace(/[^a-z0-9]/g, '-');

  return {
    filename: filename,
    full_path: folderPath + '/' + filename,
    episode_id: episodeId,
    base_name: baseName,
    target_stage: targetStage,
    force_reprocess: forceReprocess
  };
});

return episodes;

const episodeData = $('Prepare Episodes').item.json;
const statusResponse = $input.item.json;

const stages = ['discovered', 'prepped', 'transcribed', 'enriched', 'rendered'];

let currentStage = 'discovered';
let needsProcessing = true;

if (statusResponse && statusResponse.episode_id) {
  currentStage = statusResponse.stage || 'discovered';

  const currentIndex = stages.indexOf(currentStage);
  const targetIndex = stages.indexOf(episodeData.target_stage);

  if (currentIndex >= targetIndex && !episodeData.force_reprocess) {
    needsProcessing = false;
  }
}

return {
  episode_id: episodeData.episode_id,
  filename: episodeData.filename,
  full_path: episodeData.full_path,
  target_stage: episodeData.target_stage,
  force_reprocess: episodeData.force_reprocess,
  current_stage: currentStage,
  needs_processing: needsProcessing,
  status_checked: true
};

// Get the processing result from Process Episode node
const result = $input.item.json;

// All episode info should be in the result already
return {
  success: result.success || false,
  episode_id: result.episode_id,
  stage: result.stage || 'unknown',
  duration: result.duration || 0,
  error: result.error || null,
  metrics: result.metrics || null
};

const allResults = $input.all();
const successful = allResults.filter(item => item.json.success === true);
const failed = allResults.filter(item => item.json.success !== true);
const totalDuration = successful.reduce((sum, item) => sum + (item.json.duration || 0), 0);
const avgDuration = successful.length > 0 ? totalDuration / successful.length : 0;
const config = $('Set Configuration').first().json;

const summary = {
  summary_type: 'workflow_completion',
  configuration: {
    folder_path: config.folder_path,
    target_stage: config.target_stage,
    force_reprocess: config.force_reprocess
  },
  results: {
    total_videos: allResults.length,
    successful: successful.length,
    failed: failed.length,
    success_rate: ((successful.length / allResults.length) * 100).toFixed(1) + '%'
  },
  timing: {
    total_duration: totalDuration.toFixed(2) + 's',
    avg_duration: avgDuration.toFixed(2) + 's'
  },
  processed_episodes: successful.map(item => ({
    episode_id: item.json.episode_id,
    filename: item.json.filename,
    stage: item.json.stage,
    duration: item.json.duration
  })),
  failed_episodes: failed.map(item => ({
    episode_id: item.json.episode_id,
    filename: item.json.filename,
    error_message: item.json.error
  })),
  timestamp: new Date().toISOString()
};

return [...allResults, { json: summary }];
