const config = $('Set Configuration').first().json;
const commandOutput = $input.first().json.stdout;

if (!commandOutput || commandOutput.trim() === '') {
  throw new Error(`No video files found in ${config.folder_path}`);
}

// Parse the command output - each line is a filename
const videoFiles = commandOutput
  .trim()
  .split('\n')
  .map(line => line.trim())
  .filter(line => line.length > 0 && line.endsWith('.mp4'));

if (videoFiles.length === 0) {
  throw new Error(`No .mp4 files found in ${config.folder_path}`);
}

console.log(`Found ${videoFiles.length} video file(s) in ${config.folder_path}`);
videoFiles.forEach(file => console.log(`  - ${file}`));

const episodes = videoFiles.map(filename => {
  const baseName = filename.replace(/\.[^/.]+$/, '');
  const episodeId = 'newsroom-2024-' + baseName.toLowerCase().replace(/[^a-z0-9]/g, '-');

  return {
    filename: filename,
    full_path: config.folder_path + '/' + filename,
    episode_id: episodeId,
    base_name: baseName,
    target_stage: config.target_stage,
    force_reprocess: config.force_reprocess
  };
});

return episodes;

const episodeData = $('Prepare Episodes').item.json;
const statusResponse = $input.item.json;

const stages = ['discovered', 'prepped', 'transcribed', 'enriched', 'rendered'];

let currentStage = 'discovered';
let needsProcessing = true;

if (statusResponse && statusResponse.episode_id) {
  currentStage = statusResponse.stage || 'discovered';

  const currentIndex = stages.indexOf(currentStage);
  const targetIndex = stages.indexOf(episodeData.target_stage);

  if (currentIndex >= targetIndex && !episodeData.force_reprocess) {
    needsProcessing = false;
  }
}

return {
  episode_id: episodeData.episode_id,
  filename: episodeData.filename,
  full_path: episodeData.full_path,
  target_stage: episodeData.target_stage,
  force_reprocess: episodeData.force_reprocess,
  current_stage: currentStage,
  needs_processing: needsProcessing,
  status_checked: true
};

// Get the processing result from Process Episode node
const result = $input.item.json;

// Get episode data from the Split In Batches input (before Process Episode)
const episodeData = $('Split In Batches').item.json;

// Check if the API call failed
if (result.error || !result.success) {
  console.log(`âŒ FAILED: ${episodeData.episode_id}`);
  console.log(`   Error: ${result.error || result.message || 'Unknown error'}`);

  return {
    success: false,
    episode_id: episodeData.episode_id,
    filename: episodeData.filename,
    stage: 'failed',
    duration: 0,
    error: result.error || result.message || 'API call failed - check if API server is running at ' + $('Set Configuration').first().json.api_url,
    metrics: null
  };
}

// Success - return the API response with episode data
console.log(`âœ… SUCCESS: ${result.episode_id || episodeData.episode_id}`);
console.log(`   Stage: ${result.stage}`);
console.log(`   Duration: ${result.duration}s`);

return {
  success: result.success || false,
  episode_id: result.episode_id || episodeData.episode_id,
  filename: episodeData.filename,
  stage: result.stage || 'unknown',
  duration: result.duration || 0,
  error: result.error || null,
  metrics: result.metrics || null
};

const allResults = $input.all();
const successful = allResults.filter(item => item.json.success === true);
const failed = allResults.filter(item => item.json.success !== true);
const totalDuration = successful.reduce((sum, item) => sum + (item.json.duration || 0), 0);
const avgDuration = successful.length > 0 ? totalDuration / successful.length : 0;
const config = $('Set Configuration').first().json;

// Log summary to console
console.log('\n' + '='.repeat(60));
console.log('ðŸ“Š WORKFLOW SUMMARY');
console.log('='.repeat(60));
console.log(`Total Videos: ${allResults.length}`);
console.log(`âœ… Successful: ${successful.length}`);
console.log(`âŒ Failed: ${failed.length}`);
console.log(`Success Rate: ${((successful.length / allResults.length) * 100).toFixed(1)}%`);

if (failed.length > 0) {
  console.log('\nâŒ Failed Episodes:');
  failed.forEach(item => {
    console.log(`   - ${item.json.filename}: ${item.json.error}`);
  });
  console.log('\nðŸ’¡ TIP: Check if API server is running at ' + config.api_url);
}

if (successful.length > 0) {
  console.log('\nâœ… Successful Episodes:');
  successful.forEach(item => {
    console.log(`   - ${item.json.filename} (${item.json.duration.toFixed(2)}s)`);
  });
}

console.log('='.repeat(60) + '\n');

const summary = {
  summary_type: 'workflow_completion',
  configuration: {
    folder_path: config.folder_path,
    target_stage: config.target_stage,
    force_reprocess: config.force_reprocess,
    api_url: config.api_url
  },
  results: {
    total_videos: allResults.length,
    successful: successful.length,
    failed: failed.length,
    success_rate: ((successful.length / allResults.length) * 100).toFixed(1) + '%'
  },
  timing: {
    total_duration: totalDuration.toFixed(2) + 's',
    avg_duration: avgDuration.toFixed(2) + 's'
  },
  processed_episodes: successful.map(item => ({
    episode_id: item.json.episode_id,
    filename: item.json.filename,
    stage: item.json.stage,
    duration: item.json.duration
  })),
  failed_episodes: failed.map(item => ({
    episode_id: item.json.episode_id,
    filename: item.json.filename,
    error_message: item.json.error
  })),
  timestamp: new Date().toISOString()
};

return [...allResults.map(item => item.json), summary];
